package creator.soft.cygi.com.friendlyloseweighthelper.utility;import java.security.SecureRandom;import java.util.Random;public class SecureStringCreator {    char[] buffer;    private char[] VALID_CHARACTERS =            "ABCDEFGHIJKLMNPRSTUVWXYZabcdefghijklmnpqrstuvwxyz123456879".toCharArray();    private SecureRandom secureRandom = new SecureRandom();    private Random random = new SecureRandom();    public String createRandomPassword(int passwordLength) {        buffer = new char[passwordLength];        for (int i = 0; i < passwordLength; ++i) {            if ((i % 10) == 0) {                random.setSeed(secureRandom.nextLong());            }            buffer[i] = VALID_CHARACTERS[random.nextInt(VALID_CHARACTERS.length)];        }        return new String(buffer);    }}